import React, { useState, useEffect, useCallback } from 'react';

// Game settings
const GRID_SIZE = 40;
const CELL_SIZE = 40;
const INITIAL_SPEED = 100;

// Directions for both arrow keys and WASD
const DIRECTIONS = {
  // Arrow keys
  ArrowUp: { x: 0, y: -1 },
  ArrowDown: { x: 0, y: 1 },
  ArrowLeft: { x: -1, y: 0 },
  ArrowRight: { x: 1, y: 0 },
  // WASD keys
  w: { x: 0, y: -1 },
  s: { x: 0, y: 1 },
  a: { x: -1, y: 0 },
  d: { x: 1, y: 0 },
};

function SnakeGame() {
  const [snake, setSnake] = useState([{ x: 10, y: 10 }]);
  const [food, setFood] = useState({ x: 5, y: 5 });
  const [direction, setDirection] = useState(DIRECTIONS.ArrowRight);
  const [gameOver, setGameOver] = useState(false);
  const [score, setScore] = useState(0);
  const [isPaused, setIsPaused] = useState(false);

  const generateFood = () => {
    const newFood = {
      x: Math.floor(Math.random() * GRID_SIZE),
      y: Math.floor(Math.random() * GRID_SIZE),
    };
    if (snake.some(segment => segment.x === newFood.x && segment.y === newFood.y)) {
      return generateFood();
    }
    return newFood;
  };

  const handleKeyPress = useCallback((e) => {
    // Pause with spacebar
    if (e.key === ' ') {
      setIsPaused(prev => !prev);
      return;
    }

    // Get direction from key press (supports both arrows and WASD)
    const newDirection = DIRECTIONS[e.key];

    if (newDirection) {
      // Prevent 180¬∞ turns
      if (direction.x + newDirection.x !== 0 || direction.y + newDirection.y !== 0) {
        setDirection(newDirection);
      }
    }
  }, [direction]);

  useEffect(() => {
    if (gameOver || isPaused) return;

    const moveSnake = () => {
      setSnake((prevSnake) => {
        const head = { ...prevSnake[0] };
        head.x += direction.x;
        head.y += direction.y;

        // Check collisions
        if (
          head.x < 0 || head.x >= GRID_SIZE ||
          head.y < 0 || head.y >= GRID_SIZE ||
          prevSnake.some(segment => segment.x === head.x && segment.y === head.y)
        ) {
          setGameOver(true);
          return prevSnake;
        }

        const newSnake = [head, ...prevSnake];
        if (head.x === food.x && head.y === food.y) {
          setFood(generateFood());
          setScore(prev => prev + 1);
        } else {
          newSnake.pop();
        }
        return newSnake;
      });
    };

    const gameInterval = setInterval(moveSnake, INITIAL_SPEED);
    return () => clearInterval(gameInterval);
  }, [direction, food, gameOver, isPaused]);

  useEffect(() => {
    window.addEventListener('keydown', handleKeyPress);
    return () => window.removeEventListener('keydown', handleKeyPress);
  }, [handleKeyPress]);

  const resetGame = () => {
    setSnake([{ x: 10, y: 10 }]);
    setFood(generateFood());
    setDirection(DIRECTIONS.ArrowRight);
    setGameOver(false);
    setScore(0);
  };

  return (
    <div style={{ textAlign: 'center', fontFamily: 'Arial' }}>
      <h1>üêç Snake Game</h1>
      <div style={{ marginBottom: '10px' }}>
        <strong>Score: {score}</strong> | 
        <button onClick={() => setIsPaused(prev => !prev)}>
          {isPaused ? 'Resume' : 'Pause'}
        </button> |
        <button onClick={resetGame}>Restart</button>
      </div>
      <div
        style={{
          width: GRID_SIZE * CELL_SIZE,
          height: GRID_SIZE * CELL_SIZE,
          border: '2px solid #333',
          margin: '0 auto',
          position: 'relative',
          backgroundColor: '#f0f0f0',
        }}
      >
        {snake.map((segment, i) => (
          <div
            key={i}
            style={{
              position: 'absolute',
              left: segment.x * CELL_SIZE,
              top: segment.y * CELL_SIZE,
              width: CELL_SIZE,
              height: CELL_SIZE,
              backgroundColor: i === 0 ? 'darkgreen' : 'green',
              border: '1px solid white',
              borderRadius: i === 0 ? '50%' : '2px',
            }}
          />
        ))}
        <div
          style={{
            position: 'absolute',
            left: food.x * CELL_SIZE,
            top: food.y * CELL_SIZE,
            width: CELL_SIZE,
            height: CELL_SIZE,
            backgroundColor: 'red',
            borderRadius: '50%',
          }}
        />
      </div>
      {gameOver && (
        <div style={{ marginTop: '20px' }}>
          <h2>Game Over! Final Score: {score}</h2>
          <button onClick={resetGame}>Play Again</button>
        </div>
      )}
      <p>Controls: Arrow keys <strong>or</strong> WASD to move | Space to pause</p>
      <div style={{ marginTop: '10px', fontSize: '14px', color: '#666' }}>
        <p>W/‚Üë = Up | A/‚Üê = Left | S/‚Üì = Down | D/‚Üí = Right</p>
      </div>
    </div>
  );
}

export default SnakeGame;
